@roblox/globaltype/BindableFunction.Invo.code_sample:
local bf = script.Parent

-- 空函数
bf.OnInvoke = function (...) return ... end

-- 下列值可以发送至或从 BindableFunctions 发送
bf:Invoke()           -- 空
bf:Invoke(25)         -- 数字
bf:Invoke("hello")    -- 字符串
bf:Invoke(true)       -- 布尔值
bf:Invoke("buy", 25)  -- 可以是多个值
bf:Invoke{1, 2, 3}    -- 无间隙数组形式的表
                      -- 注意大括号的使用
bf:Invoke{            -- 仅包含字符串键的表
	hello = "world";
	goodbye = "world";
}
bf:Invoke{            -- 包含字符串键的表
	point = {1, 2};   -- 其值也有效
	point2 = {3, 4};
}
bf:Invoke{            -- 数组形式的表
	{1, 2, 3};        -- 其值也有效
	{hello = "world";};
}

-- 下列值无法发送至或从 BindableFunctions 发送
bf:Invoke{1, nil, 3}  -- 数组形式的表不能有空间隙
bf:Invoke{
	[{}] = "hello";   -- 表键只能为数字或字符串
}
bf:Invoke{            -- 表键不可同时包含数字和字符串
	[1] = "apple";
	hello = "world";
}

